806 static void load_procs(GL3WGetProcAddressProc proc)
807 {
808     size_t i;
809     for (i = 0; i < GL3W_ARRAY_SIZE(proc_names); i++)
810         imgl3wProcs.ptr[i] = proc(proc_names[i]);
811 }
